import React,{Component} from 'react';
import {Switch,Route,Redirect} from 'react-router-dom';
import Course from './view/course/index';
import Home from './view/home/index';
import Registered from './view/registered/index';
import Login from './view/login/index';
import MessAge from './view/message/index';
import Work from './view/work/index';
import './common/css/style.css';

class App extends Component{
    constructor(props){
        super(props);
        this.state={
            name:""
        }
    }

    login=(userValue,passWordValue)=>{
        if(!userValue||!passWordValue)return false;
        this.setState({
            name:userValue
        })
    }


    render(){
        let{name}=this.state;
        return(
            <Switch>
                <Route exact path='/' render={
                    ()=>{
                        return <Home name={name}/>
                    }
                }/>
                <Route path='/course' component={Course}/>
                <Route path='/registered' component={Registered}/>
                <Route path='/work' component={Work}/>
                <Route exact path='/login' render={
                    ()=>{
                        return name?<Redirect to='/' />:<Login Click={this.login}/>
                    }
                }/>
                <Route path='/message' component={MessAge}/>
            </Switch>
        )
    }
}
export default App;
