import React,{Component,createRef} from 'react';
// import Header from '../../common/component/header';
// import Footer from '../../common/component/footer';
// import Menu from '../../common/component/menu';
let ArrImage=[require('../../common/img/tab/img1.png'),
require('../../common/img/tab/img2.png'),
require('../../common/img/tab/img3.png'),
require('../../common/img/tab/img4.png'),require('../../common/img/tab/img1.png')];
var i=0;
var num=[1,2,3,4];
var j=0;
var tho=null;
export default class Home extends Component{
    constructor(props){
        super(props);
        this.state={

        }
        this.Div=createRef();
        this.navList=createRef();
    }
    componentDidMount(){
        this.navList.current.children[j].style.cssText='background:red';
        let _this=this;
        setInterval(function(){
           if(i===-30)
            {
                i=0;
                _this.Div.current.style.left=i+'rem';
                _this.Div.current.style.transition='none'
                _this.navList.current.children[j].style.cssText='background:red'
            }
            else{
                if(tho||this.navList.current.children[j])
                {
                    tho.style.cssText='background:white'
                }
                this.navList.current.children[0].style.cssText='background:white';
                i-=7.5;
                _this.Div.current.style.left=i+'rem';
               _this.Div.current.style.transition='1s';
                j++;
                _this.navList.current.children[j].style.cssText='background:red' 
                tho= _this.navList.current.children[j];
            }
        },1000)
    }
    render(){
        let aimg=ArrImage.map(function(item){
            return <li  key={Math.random()}><img src={item} alt=''/></li>
        })
        let navList=num.map(function(){
            return <span key={Math.random()}></span>
        })
        return(
            <div>
                <div className='tab'>
                    <ul className='tabTable' ref={this.Div}>
                        {aimg}
                    </ul>
                    <div className='navList' ref={this.navList}>
                        {navList}
                    </div>
                </div>
            </div>
        )
    }
}
