import React,{Component,createRef} from 'react';
import {Link} from 'react-router-dom';
export default class Home extends Component{
    constructor(props){
        super(props);
        this.state={
            userValue:"",
            passWordValue:""
        }
        this.userInput=createRef();
        this.passWordInput=createRef();
    }

    userInput=()=>{
        this.setState({
            userValue:this.userInput.current.value
        })
    }

    passWordValue=()=>{
        this.setState({
            userValue:this.passWordInput.current.value
        })
    }

    render(){
        let{Click}=this.props;
        let{userValue,passWordValue}=this.state;
        return(
            <div id='login'>
                <h1/>
                <div className='loginBg'>
                    <div className='loginPrompt'>
                        <i className='iconfont icon-mima'></i>
                        <span>如有账号，请直接登录</span>
                    </div>
                    <div className='Login_Wid clear'>
                        <div className='Input_user'>
                            <i className='iconfont icon-youxiang'></i>
                            <input placeholder='请输入用户名' ref={this.userInput} onChange={this.userInput} value={userValue}/>
                        </div>
                        <div className='Input_user'>
                            <i className='iconfont icon-mima'></i>
                            <input placeholder='请输入密码' ref={this.passWordInput} onChange={this.passWordValue} value={passWordValue}/>
                        </div>
                        <div className='foot'>
                            <input type="checkbox"/>
                           <span>记住密码</span>
                        </div>
                        <div className='footRig'>
                            <Link to=''><span>忘记密码？</span></Link>
                        </div>
                        <div className='loginBtn'>
                           <a href='javascript:;' onClick={()=>{
                               Click()
                           }}>登录</a>
                        </div>
                        <p>没有账号？<Link to=''><span>立即注册</span></Link></p>
                    </div>
                </div>
            </div>
        )
    }
}
