import React,{Component} from 'react';
import Header from  '../../common/component/header';
import Meun from '../../common/component/menu';
import Footer from '../../common/component/footer';
import Li from '../../common/component/li';
var g=[1,3,4];
var i=0;
export default class Lecturer extends Component{
    componentDidMount(){
        var m=document.querySelector('.naMove');
        console.log(m)
        setInterval(function(){
            var m=document.querySelector('.naMove');
            i-=7.5
            m.style.left=`${i}+rem`
        },1000)
    }
    render(){
        let{click}=this.props;
        return(
            <div>
                <Header click={(e)=>{click(e)}} defaultProps={true} bool={true}/>
                <Meun/>
                <div className='na'>
                    <i></i>
                    <div className='naMove'>
                        <ul className='lect'>
                           {g.map(item=><Li key={Math.random()}/>)}
                        </ul>
                        <ul className='lect'>
                           {g.map(item=><Li key={Math.random()}/>)}
                        </ul>
                    </div>
                </div>
                <Footer/>
            </div>
        )
    }
}
