import React,{Component,Fragment,createRef} from 'react';
import {Link} from  'react-router-dom';
import PublicLCom from '../../common/component/publicLogin';
export default class registered extends Component{
    constructor(props){
        super(props);
        this.state={
            userValue:'',
            passWordValue:'',
            ConfirmPassword:'',
            study:''
        }
        this.userInput=createRef();
        this.passWordInput=createRef();
        this.ConfirmPasswordInput=createRef();
        this.studyInput=createRef();
    }
    userIn=()=>{
        if(this.userInput.current.value)
        {
            this.setState({
                userValue:this.userInput.current.value
            })
        }
    } 

    passWordIn=()=>{
        this.setState({
            passWordValue:this.passWordInput.current.value
        })
    }

    ConfirmPasswordIn=()=>{
        this.setState({
            ConfirmPassword:this.ConfirmPasswordInput.current.value
        })
    }
    studyIn=()=>{
        this.setState({
            study:this.studyInput.current.value
        })
    }

    render(){
        let{userValue,passWordValue,ConfirmPassword,study}=this.state
        console.log(userValue,passWordValue,ConfirmPassword,study)
    return(
        <Fragment>
            <PublicLCom>
                <div className='register'>
                    <h5 className='register_title'>注册账号</h5>
                    <ul className='registerInput clear'>
                        <li>
                            <em className='user'>用户名</em>
                            <input onChange={this.userIn} ref={this.userInput} value={userValue}/>
                        </li>
                        <li>
                            <em>设置密码</em>
                            <input onChange={this.passWordIn} ref={this.passWordInput} value={passWordValue}/>
                        </li>
                        <li>
                            <em>确定密码</em>
                            <input onChange={this.ConfirmPasswordIn}ref={this.ConfirmPasswordInput} value={ConfirmPassword}/>
                        </li>
                        <li>
                            <em>学习宣言</em>
                            <input onChange={this.studyIn} ref={this.studyInput} value={study}/>
                        </li>
                    </ul>
                    <div className='register_btn'>
                        <a href='javascript:;'>马上注册</a>
                    </div>
                    <p className='register_login'>已有账号？<Link to='/login'>立即登录</Link></p>
                </div>
            </PublicLCom>
        </Fragment>
    )
    }
}
