import Header from '../../common/component/header';
import Footer from '../../common/component/footer';
import Menu from '../../common/component/menu';
let ArrImage=[require('../../common/img/tab/img1.png'),
require('../../common/img/tab/img2.png'),
require('../../common/img/tab/img3.png'),
require('../../common/img/tab/img4.png')];
var i=0;
export default class Home extends Component{
    constructor(props){
        super(props);
        this.state={

        }
        this.Div=createRef();
    }
    componentDidMount(){
        let _this=this;
        console.log(_this.Div)
        setInterval(function(){
             _this.Div.current.style.left=i+'px';
             i+=7.5
        },1000)
    }
    render(){
        let aimg=ArrImage.map(function(item){
            return <img src={item} alt='' key={Math.random()}/>
        })
        return(
            <div>
                {/* <Header/>
                <Footer/> */}
                {/* < Menu /> */}
                <div className='tab'>
                    <div className='tabTable clear' ref={this.Div}>
                       {aimg}
                    </div>
                </div>
            </div>
        )
    }
}
